$(document).ready(function(){
	
	/*
	*******************************
		Menu Dropdown
	*******************************
	*/
	$("nav#menu ul li").hover(function(){
		$(this).addClass("hover");
		$('ul:first',this).css({visibility: "visible", display: "none"}).fadeIn(150);
	}, function(){
		$(this).removeClass("hover");
		$('ul:first',this).css('visibility', 'hidden');
	});
	$("nav#menu ul li ul li:has(ul)").find("a:first").append("&raquo;"); 
	
	/*
	*******************************
		Tabs
	*******************************
	*/ 
	$("section#tabs ul li").click(function(){
		$("section#tabs ul li").removeClass('active');
		$(this).addClass("active");
		$("section#tabs #tabs-content div").hide();
		
		var selected_tab = $(this).find("a").attr("href");
		$(selected_tab).fadeIn(300);
		
		return false;
		
	});
	
	/*
	*******************************
		Accordion
	*******************************
	*/
	$('section#accordion ul li div.content').hide();
	
	$('section#accordion ul li a').click(function(){
		$('section#accordion ul li div.content').slideUp();		
		$('section#accordion ul li a').each(function(){
			$(this).removeClass('active');
			//$(this).removeClass($(this).attr('rel') + 'Over');
		});
		
		$(this).siblings('div.content').slideDown('fast');
		$(this).addClass('active');
			
		return false;	
	});	
	
	/*
	*******************************
		Slider
	*******************************
	*/
	$("#slider").slideshow({
		width      : 900,
		height     : 325,
		transition : 'square'
		//transition : 'SlipLeft'
	});
	
	/*
	*******************************
		Shadowbox
	*******************************
	*/
	Shadowbox.init({
		overlayOpacity: 0.85,
		displayCounter: true, 
		displayNav: true, 
		modal: false
	});
	
	/*
	*******************************
		Tipsy
	*******************************
	*/	
	$('#north').tipsy({gravity: 'n'});
	$('#south').tipsy({gravity: 's'});
	$('#east').tipsy({gravity: 'e'});
	$('#west').tipsy({gravity: 'w'});
	$('#north-west').tipsy({gravity: 'nw'});
	$('#north-east').tipsy({gravity: 'ne'});
	$('#south-west').tipsy({gravity: 'sw'});
	$('#south-east').tipsy({gravity: 'se'});
 
	
});
